<template>
    <template v-if="state">
        <SplashAd :ad-type="adType" :ad-url="adUrl" :duration="duration" :redirect-type="redirectType" :redirect-url="redirectUrl" @close="onSplashClose" />
    </template>
    <tig-layout v-else>
        <template v-if="configStore.previewId > 0">
            <previewTip />
        </template>

        <template v-if="loading">
            <view class="index_empty">
                <image lazy-load :src="staticResource('common/index_empty.png')" mode="widthFix" />
            </view>
        </template>
        <template v-else>
            <view class="index">
                <view class="decorate-page-window" :style="{ ...cssVariable }">
                    <modules :modules="modulesData" :page-modles="pageModulesData" :scroll-top="scrollTop" @load-goods-list="loadGoodsList" />
                    <!-- 加载商品模块 -->
                    <template v-if="currentCategoryId > 0 && commodityList.length > 0">
                        <masonry :commodity-list="commodityList" />

                        <template v-if="!bottomLoading && commodityList.length === 0">
                            <view class="empty-box">
                                <up-empty :icon="staticResource('salesman/no_order.png')" :text="$t('暂无数据')" />
                            </view>
                        </template>
                    </template>
                </view>
            </view>
        </template>
        <tig-back-top :scroll-top="scrollTop" />
    </tig-layout>
</template>

<script setup lang="ts">
import { ref, computed } from "vue";
import { onLoad, onReachBottom, onPullDownRefresh, onShow, onShareAppMessage, onShareTimeline, onUnload, onPageScroll } from "@dcloudio/uni-app";
import { getIndex, getSplashAd } from "@/api/home/home";
import modules from "@/components/modules/index.vue";
import SplashAd from "@/components/modules/SplashAd/index.vue";
import masonry from "@/components/masonry/masonry.vue";
import previewTip from "./src/previewTip.vue";
import { getCateProduct } from "@/api/home/home";
import type { GetProductFilterResult } from "@/types/home/home";
import { useConfigStore } from "@/store/config";
import { usecatnavStore } from "@/store/catnav";
import { staticResource } from "@/utils";
import checkAppUpdate from "@/utils/checkAppUpdate";
import { useScrollTop } from "@/hooks";
import type { PageModule } from "@/types/home/home";

const { scrollTop } = useScrollTop(onPageScroll);

const configStore = useConfigStore();
const adType = ref<"image" | "video">("image");
const adUrl = ref("");
const duration = ref(0);
const state = ref(false);
const redirectType = ref("");
const redirectUrl = ref("");
const showCatNav = ref(0);
const loading = ref(false);
const modulesData = ref<any[]>([]);
const pageModulesData = ref<PageModule>({} as PageModule);
const currentCategoryId = ref(0);
const page = ref(1);
const loadend = ref(false);
const bottomLoading = ref(true);
const commodityList = ref<GetProductFilterResult[]>([]);

const cssVariable = computed(() => {
    return {
        "background-color": `${configStore.pageModules?.backgroundColor}`,
        "background-image": `url("${configStore.pageModules.backgroundImage?.picUrl}")`,
        "background-size": `${getSize(configStore.pageModules?.backgroundSize)}`,
        "background-repeat": `${getRepeat(configStore?.pageModules?.backgroundRepeat)}`
    };
});

const getSize = (key: string) => {
    let repeat: { [key: string]: any } = {
        "1": "100% auto",
        "2": "auto 100%",
        "3": "100% 100%"
    };
    return repeat[key];
};

const getRepeat = (key: string) => {
    let repeat: { [key: string]: any } = {
        "1": "no-repeat",
        "2": "repeat",
        "3": "repeat-x",
        "4": "repeat-y"
    };
    return repeat[key];
};

const loadGoodsList = (categoryId: number) => {
    currentCategoryId.value = categoryId;
    if (currentCategoryId.value === 0) return;
    page.value = 1;
    loadend.value = false;
    commodityList.value = [];
    getProductList();
};
const getProductList = async () => {
    bottomLoading.value = true;
    try {
        const result = await getCateProduct({ categoryId: currentCategoryId.value, page: page.value });
        if (result.records && result.records.length > 0) {
            commodityList.value = [...commodityList.value, ...result.records];
            loadend.value = false;
        } else {
            loadend.value = true;
        }
    } catch (error) {
        console.error(error);
    } finally {
        bottomLoading.value = false;
    }
};

const getIndexData = async () => {
    loading.value = true;
    try {
        const res = await getIndex();
        modulesData.value = res.moduleList;
        pageModulesData.value = res.pageModule;
        configStore.pageModules = res.pageModule;
        showCatNav.value = 1;
        uni.stopPullDownRefresh();
    } catch (error) {
        console.error(error);
    } finally {
        loading.value = false;
    }
};

const getSplashAdData = async () => {
    try {
        const result = await getSplashAd();
        if (!result) return;
        state.value = result.state == 1 ? true : false;
        adType.value = result.materialType == 0 ? "image" : "video";
        adUrl.value = result.materialType == 0 ? result.materialImg : result.materialVideo[0].videoUrl;
        redirectType.value = String(result.redirectType);
        redirectUrl.value = result.redirectUrl;
        duration.value = result.maxWaitTime;
    } catch (error) {
        console.error("获取开屏广告失败:", error);
    }
};

const onSplashClose = () => {
    state.value = false;
};

onLoad(async (options: any) => {
    if (options && options.previewId) {
        configStore.previewId = options.previewId;
    }
    catnavStore.reset();
    await getSplashAdData();
    getIndexData();

    // #ifdef APP-PLUS || APP-HARMONY
    checkAppUpdate();
    // #endif
});

onUnload(() => {
    catnavStore.reset();
});

const catnavStore = usecatnavStore();
onPullDownRefresh(() => {
    currentCategoryId.value = 0;
    catnavStore.reset();
    getIndexData();
});

onReachBottom(() => {
    if (currentCategoryId.value > 0 && !loading.value && !loadend.value) {
        page.value++;
        getProductList();
    }
});

onShow(() => {
    if (scrollTop.value > 0) {
        setTimeout(() => {
            uni.pageScrollTo({
                scrollTop: scrollTop.value,
                duration: 0
            });
        }, 20);
    }
    uni.hideTabBar();
});

onShareAppMessage((res) => {
    return {
        title: configStore.baseInfo.shopTitle
    };
});
onShareTimeline(() => {
    return {
        title: configStore.baseInfo.shopTitle
    };
});
</script>
